public static string ReplaceEach(Match m)
{
    string dbType = m.Groups[1].Value;
    dbType = dbType.Split('(')[0];
    string csType  = toCsType(dbType.ToLower());
    return "public " + csType;
}
private static string toCsType(string dbType){
switch(dbType){
case "decimal":
case "long":
case "int": return dbType;
case "date":
case "datetime": return "DateTime";
default: return "string";
}

}
